CREATE DATABASE `carrefour`;

USE `carrefour`;

DROP TABLE IF EXISTS `goods`;

CREATE TABLE `goods` (
  `category` varchar(10) COMMENT '類別',
  `lowprice` varchar(10) COMMENT '最低價格',
  `higtprice` varchar(10) COMMENT '最高價格',
  `area` varchar(10) COMMENT '區域',
  `lr` varchar(10) COMMENT '左1右2',
  `numbers` varchar(10) COMMENT '櫃子數',
  `uda` int COMMENT '上1下2全3',
  `remarks` varchar(70) COMMENT '備註'
) COMMENT = '家樂福商場資料';

